    private void InvisibleMouseReleased(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_InvisibleMouseReleased
    Visible.setVisible(false);
    Invisible.setVisible(true);
    JSenha.setEchoChar('*'); 
    }//GEN-LAST:event_InvisibleMouseReleased

