    private void JentrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_JentrarActionPerformed
        String email = jUser.getText();  // Lê o input do Usuário
        String senha = new String(JSenha.getPassword()); // Lê a senha
        
        UsuarioDAO dao = new UsuarioDAO();
        
        if (dao.autenticar(email, senha)) {
        JOptionPane.showMessageDialog(this, "Sucesso");
        
        // Entra para tela_Principal
        new House_ClassUp().setVisible(true);
        this.dispose();
    } else {
        
        JOptionPane.showMessageDialog(this, "Email ou Senha inválidos!");
        jUser.setText("");
        JSenha.setText("");
            }
    }//GEN-LAST:event_JentrarActionPerformed

